import axios from 'axios';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'
import './login-signup.css'

function LoginForm() {

  const auth = {
                  name: 'Ayush',
                  email:'ayush.yadav@ens.enterprises',
                  password: 'Ayush123'
                }



  const[userLogin, setUserlogin] = useState({ email:'', password:''});
  const onInputHandler = (e) =>{
    setUserlogin({...userLogin, [e.target.name]: e.target.value})
  }

  const onSubmit = async () =>{

    try {
      const {data : {message, status , token}} = await axios({
        url: '/login',
        method: 'post',
        data: userLogin
      });


    } catch (error) {
      console.log('authentication error...', error)
    }

    switch (message) {
      case "flag3":
          return alert('Vatidation Fialed');
      case "flag0":
        return alert('All fields are required')
      case "flag00":
        return alert('Looks Server is Down')
      case "flag000":

      case "flag0000":

      case "flag1":
          return redirectHandler(token);
  }

    if(auth.email !== userLogin.email && auth.password !== userLogin.password){
      alert('User does not exist')
    }
    else if(auth.email !== userLogin.email){
      alert('No user found with this email')
    }
    else if(auth.password !== userLogin.password){
      alert('Wrong Password')
    }
    else{
      sessionStorage.setItem('login', token);
      navigate('/');

    }

  }

  const navigate = useNavigate();

  console.log(userLogin)

  return (
    <div style={{display:'flex',justifyContent:'center', height:'fitContent'}}>
    <div className="form">
        <div className='form_container' style={{padding:'0 1rem'}}>
          <h2>Log In</h2>
          <form onSubmit={onSubmit} >
          <input name='email' onChange={(e)=>onInputHandler(e)} required type='email' placeholder='Email' />
          <input name='password' onChange={(e)=>onInputHandler(e)} required type='password' placeholder='Password' />
            <Link to='/' style={{float:'right'}}>Forgot Password</Link>
        <input type='submit' />
        <h3>OR</h3>
        <Link to='/signup' ><div className='newUser_btn'>New User?</div></Link>
        </form>
        </div>
    </div>
    </div>
  )
}

export default LoginForm;
