import './cart.css';
import { useSelector } from 'react-redux';

function Cart({ cart, setCart }) {

  const cartProducts = useSelector((state)=> state.cart);

  return (
    <>
    {
        cart &&
        <div style={{height:'100vh',width:'100vw',background:'transparent',position:'absolute'}}>
            <div className='cart_container'>
                <div className='cart_header'>
                    <span onClick={()=> setCart(!cart)} className='icon'>&#10060;</span>
                    <h2>Your Cart (2)</h2>
                </div>
                <div>
                  {
                    cartProducts.map((product)=>
                      <div className='cart_items'>
                        <img src={product.image} alt='product' width='100px' height='120px' />
                        <div className=' cart_items'>
                          <h3>{product.title}</h3>
                          <p style={{color:'green'}}>Rs. {product.price}</p>
                        </div>
                      </div>
                    )
                  }
                </div>
            </div>
        </div>
    }
    </>
  )
}

export default Cart
